// TrialPopup.js - User interface for trial warnings and expiry
import React, { useState } from 'react';
import './TrialPopup.css';

const TrialPopup = ({ 
  trialManager, 
  onClose,
  onExtended,
  type = 'warning' // 'warning', 'expired', or 'info'
}) => {
  const [showExtensionForm, setShowExtensionForm] = useState(false);
  const [extensionCode, setExtensionCode] = useState('');
  const [email, setEmail] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Get trial statistics
  const stats = trialManager.getStats();
  const remainingDeals = trialManager.getRemainingDeals();

  // Handle sending feedback email
  const handleSendFeedback = () => {
    const emailData = trialManager.generateFeedbackEmail();
    if (emailData) {
      const mailtoLink = `mailto:mike.smith@example.com?subject=${emailData.subject}&body=${emailData.body}`;
      window.open(mailtoLink);
    }
  };

  // Handle extension code submission
  const handleExtensionSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const result = trialManager.extendTrial(extensionCode, email);
      
      if (result.success) {
        alert(`Success: ${result.message}`);
        onExtended && onExtended();
        onClose();
      } else {
        alert(`Error: ${result.message}`);
      }
    } catch (error) {
      alert('An error occurred while processing the extension code.');
    } finally {
      setIsSubmitting(false);
    }
  };

  // Get popup content based on type
  const getContent = () => {
    if (type === 'expired') {
      return {
        title: '‚è∞ Trial Period Expired',
        message: `Your evaluation period has ended after ${stats.maxDeals} deals. Thank you for testing Bonus Bridge!`,
        emoji: 'üö´',
        color: 'red'
      };
    } else if (type === 'warning') {
      return {
        title: '‚ö†Ô∏è Trial Nearly Complete',
        message: `You have ${remainingDeals} deal${remainingDeals === 1 ? '' : 's'} remaining in your evaluation period.`,
        emoji: '‚ö†Ô∏è',
        color: 'orange'
      };
    } else {
      return {
        title: 'üéØ Welcome to Bonus Bridge',
        message: `You have ${remainingDeals} deals to evaluate the application.`,
        emoji: 'üéØ',
        color: 'blue'
      };
    }
  };

  const content = getContent();

  return (
    <div className="trial-popup-overlay">
      <div className={`trial-popup trial-popup-${content.color}`}>
        {/* Header */}
        <div className="trial-popup-header">
          <h2>{content.title}</h2>
          {type !== 'expired' && (
            <button className="close-btn" onClick={onClose}>√ó</button>
          )}
        </div>

        {/* Content */}
        <div className="trial-popup-content">
          <div className="trial-message">
            <span className="trial-emoji">{content.emoji}</span>
            <p>{content.message}</p>
          </div>

          {/* Usage Statistics */}
          {stats && (
            <div className="trial-stats">
              <h3>üìä Your Usage</h3>
              <div className="stats-grid">
                <div className="stat-item">
                  <span className="stat-label">Deals Played:</span>
                  <span className="stat-value">{stats.dealsPlayed}/{stats.maxDeals}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Games Completed:</span>
                  <span className="stat-value">{stats.gamesCompleted}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Days Used:</span>
                  <span className="stat-value">{stats.daysUsed}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Started:</span>
                  <span className="stat-value">{stats.startDate}</span>
                </div>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="trial-actions">
            {(type === 'warning' || type === 'expired') && (
              <>
                <button 
                  className="feedback-btn primary"
                  onClick={handleSendFeedback}
                >
                  üìß Send Feedback & Request Extension
                </button>

                <button 
                  className="extension-btn secondary"
                  onClick={() => setShowExtensionForm(!showExtensionForm)}
                >
                  üîë I Have an Extension Code
                </button>
              </>
            )}

            {type === 'info' && (
              <button className="continue-btn primary" onClick={onClose}>
                Start Evaluating
              </button>
            )}

            {type === 'warning' && (
              <button className="continue-btn secondary" onClick={onClose}>
                Continue Playing
              </button>
            )}
          </div>

          {/* Extension Form */}
          {showExtensionForm && (
            <div className="extension-form">
              <h3>üîë Enter Extension Code</h3>
              <form onSubmit={handleExtensionSubmit}>
                <div className="form-group">
                  <label htmlFor="email">Your Email:</label>
                  <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    placeholder="your.email@example.com"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="code">Extension Code:</label>
                  <input
                    type="text"
                    id="code"
                    value={extensionCode}
                    onChange={(e) => setExtensionCode(e.target.value)}
                    required
                    placeholder="Enter code from Mike"
                  />
                </div>
                <div className="form-actions">
                  <button 
                    type="button" 
                    className="cancel-btn"
                    onClick={() => setShowExtensionForm(false)}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="submit-btn"
                    disabled={isSubmitting}
                  >
                    {isSubmitting ? 'Checking...' : 'Extend Trial'}
                  </button>
                </div>
              </form>
            </div>
          )}

          {/* Expired Message */}
          {type === 'expired' && (
            <div className="expired-note">
              <p><strong>Note:</strong> You can still view previous games, but cannot start new deals without an extension code.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TrialPopup;
