import React, { useContext, useEffect, useState } from 'react';
import { GameProvider, GameContext } from './context/GameContext';
import ContractInput from './components/ContractInput';
import TricksInput from './components/TricksInput';
import ScoreAdjustment from './components/ScoreAdjustment';
import ScoreTracking from './components/ScoreTracking';
import './styles/global.css';

// Main application content
const AppContent = () => {
  const { gameState } = useContext(GameContext);
  const [wakeLock, setWakeLock] = useState(null);
  const [wakeLockSupported, setWakeLockSupported] = useState(false);
  const [wakeLockEnabled, setWakeLockEnabled] = useState(false);

  // Check if Wake Lock API is supported
  useEffect(() => {
    const checkWakeLockSupport = () => {
      if ('wakeLock' in navigator) {
        setWakeLockSupported(true);
      } else {
        console.log('Wake Lock API not supported');
        setWakeLockSupported(false);
      }
    };

    checkWakeLockSupport();
  }, []);

  // Request wake lock
  const requestWakeLock = async () => {
    if (!wakeLockSupported) return;

    try {
      const lock = await navigator.wakeLock.request('screen');
      setWakeLock(lock);
      setWakeLockEnabled(true);
      
      // Listen for wake lock release
      lock.addEventListener('release', () => {
        setWakeLockEnabled(false);
      });
      
      console.log('Wake Lock is active');
    } catch (err) {
      console.error(`Failed to get wake lock: ${err.message}`);
    }
  };

  // Release wake lock
  const releaseWakeLock = () => {
    if (wakeLock) {
      wakeLock.release()
        .then(() => {
          setWakeLock(null);
          setWakeLockEnabled(false);
          console.log('Wake Lock released');
        })
        .catch((err) => {
          console.error(`Failed to release wake lock: ${err.message}`);
        });
    }
  };

  // Toggle wake lock
  const toggleWakeLock = () => {
    if (wakeLockEnabled) {
      releaseWakeLock();
    } else {
      requestWakeLock();
    }
  };

  // Re-request wake lock if page visibility changes
  useEffect(() => {
    const handleVisibilityChange = () => {
      if (wakeLockEnabled && document.visibilityState === 'visible') {
        requestWakeLock();
      }
    };

    document.addEventListener('visibilitychange', handleVisibilityChange);
    return () => {
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [wakeLockEnabled]);

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>BonusBridge</h1>
        <div className="version-tag">Evaluation (100 deals)</div>
        
        {wakeLockSupported && (
          <button
            className={`wake-lock-button ${wakeLockEnabled ? 'active' : ''}`}
            onClick={toggleWakeLock}
          >
            {wakeLockEnabled ? 'Screen Lock On' : 'Keep Screen On'}
          </button>
        )}
      </header>
      
      <main className="app-content">
        <ContractInput />
        <TricksInput />
        <ScoreAdjustment />
        <ScoreTracking />
      </main>
      
      <footer className="app-footer">
        <p>BonusBridge Â© 2025 - Offline Bridge Scoring</p>
        <p>Deals: {gameState.dealCount}/100</p>
      </footer>
    </div>
  );
};

// Main App component wrapped with GameProvider
const App = () => {
  // Register service worker for PWA
  useEffect(() => {
    if ('serviceWorker' in navigator) {
      window.addEventListener('load', () => {
        navigator.serviceWorker.register('/serviceWorker.js')
          .then((registration) => {
            console.log('ServiceWorker registration successful:', registration.scope);
          })
          .catch((error) => {
            console.log('ServiceWorker registration failed:', error);
          });
      });
    }
  }, []);

  return (
    <GameProvider>
      <AppContent />
    </GameProvider>
  );
};

export default App;
